class CurveGenerator:
    @staticmethod
    def bezier(control_points, values=np.linspace(0.0, 1.0, 100), points=np.array([[0, 0], [1, 0], [1, 1], [0, 1]])):
        nodes = np.asfortranarray(nodes)
        bezier_curve = bezier.Curve(nodes.T, degree=nodes.shape[0] - 1)
        return bezier_curve.evaluate_multi(values).T
    @staticmethod
    def math_function(function, values, input_coordinate="x_input", polar=False):
        if input_coordinate == "x_input":
            x_values = values
            y_values = np.array([function(x) for x in x_values])
            curve = np.vstack((x_values, y_values)).T
        elif input_coordinate == "y_input":
            y_values = values
            x_values = np.array([function(y) for y in y_values])
            curve = np.vstack((x_values, y_values)).T
        elif input_coordinate == "parametric":
            result = [function(value) for value in values]
            curve = np.array(result)
        else:
            print("Invalid input_coordinate, curve will default to 0,0")
            curve = np.array([[0.0, 0.0]])
        if polar:
            curve = CurveTransforms.polar_to_cartesian(curve)
        return curve
    @staticmethod
    def regular_polygon(n, radius=1.0, offset=(0.0, 0.0)):
        theta = np.linspace(0, 2 * np.pi, n + 1)
        rho = radius * np.ones(n + 1)
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def edge_type1(width=1.0, height=0.2, flip=False, half=False):
        if flip:
            height = -height
        triangle1 = np.array([[0, 0], [0.1*width, height], [0.2*width, 0]])
        triangle2 = np.array([[0.8*width, 0], [0.9*width, height], [width, 0]])
        curve_nodes = np.array([[0.2*width, 0], [0.4*width, height], [0.6*width, height], [0.8*width, 0]])
        curve = CurveGenerator.bezier(curve_nodes, np.linspace(0, 1 - 0.5 * half, 100))
        if half:
            return CurveTransforms.merge_curves([triangle1, curve])
        return CurveTransforms.merge_curves([triangle1, curve, triangle2])
    @staticmethod
    def random_edge(grid_size=(1,1), grid_step=(0.1, 0.1), interpolation_type="bezier"):
        control_nodes = []
        for i in range(grid_size[0] + 1):
            control_nodes.append([i * grid_step[0], np.random.randint(0, grid_size[1] + 1) * grid_step[1]])
        control_nodes = np.array(control_nodes)
        if interpolation_type == "bezier":
            curve = CurveGenerator.bezier(control_nodes)
        elif interpolation_type == "linear":
            curve = control_nodes
        else:
            curve = CurveTransforms.interpolate(control_nodes, 100, "cubic", False)
        return curve
    @staticmethod
    def polyline(x_values, y_values):
        return np.vstack((np.array(x_values), np.array(y_values))).T
    @staticmethod
    def circular_arc(n=100, radius=0.5, theta1=0.0, theta2=np.pi / 2, offset=(0.0, 0.0)):
        theta = np.linspace(theta1, theta2, n)
        rho = np.ones(100) * radius
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def sine_wave_circular_arc(n=100, radius=0.5, theta1=0.0, theta2=np.pi/2, amplitude=0.1, n_periods=2, period_offset=0.25, offset=(0.0, 0.0)):
        theta = np.linspace(theta1, theta2, n)
        period = (theta2 - theta1)/n_periods
        freq = 2*np.pi/period
        rho = radius + amplitude*np.sin((theta-theta1+period_offset*period)*freq)
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def petal(n=100, width=0.1, height=0.2, flat_stop_height=0.1, half=True):
        arc = CurveGenerator.circular_arc(n, width / 2, 0, np.pi, (0, flat_stop_height))
        CurveTransforms.scale_single(arc, (1, 2 * (height - flat_stop_height) / width), (0, flat_stop_height))
    @staticmethod
    def leaf(values=np.linspace(0, 1, 100), width=0.1, height=0.2, half=False):
        control_nodes = np.array([[0, 0], [0, 1], [-1, 1], [-1, 2], [0, 2], [0, 3]])
        curve = CurveGenerator.bezier(control_nodes, values)
        curve = CurveTransforms.fit_c2c(curve, (-width/2, 0, 0, height))
        if not half:
            curve = np.vstack((curve, np.flip(CurveTransforms.scale_single(curve, (-1, 1), inplace=False), 0)))
        return curve
    @staticmethod
    def pit(n=100, width=0.8, gap=0.2, repetitions=1):
        start = -(width+gap)/2
        arc_start = -width/2
        end = -start
        arc_end = -arc_start
        gap1 = np.linspace(start, arc_start, 10)
        gap2 = np.linspace(arc_end, end, 10)
        gap1 = np.vstack((gap1, np.zeros(10))).T
        gap2 = np.vstack((gap2, np.zeros(10))).T
        arc = CurveGenerator.circular_arc(n, width/2, 0, np.pi)
        CurveTransforms.scale_single(arc, (-1, -1))
        curve = CurveTransforms.merge_curves([gap1, arc, gap2])
        curves = []
        for i in range(repetitions):
            curves.append(CurveTransforms.translate_single(curve, i*(width+gap), 0, False))
        return CurveTransforms.merge_curves(curves)

class ImageSample:
    outline_colors = [plt.get_cmap("tab20")(i/20) for i in range(20)]
    random.shuffle(outline_colors)
    current_outline = 0
    def __init__(self, image, extent, main_clip_path, local_outline_curves: List[np.ndarray] = None):
        self.Image = image
        self.Extent = extent
        self.MainClipPath = main_clip_path
        self.PersistentClipPaths = [main_clip_path]
        self.PersistentTransforms = [Affine2D()]
        self.AdditiveTransforms = []
        if local_outline_curves:
            self.LocalOutlineCurves = local_outline_curves
        else:
            self.LocalOutlineCurves = []
        self.AdditionalCurves = []
    def clear_additive_transforms(self):
        self.AdditiveTransforms = []
    def add_additive_transform(self, transform):
        self.AdditiveTransforms.append(transform)
    def add_additive_transforms(self, transforms):
        self.AdditiveTransforms.extend(transforms)
    def add_additional_curve(self, curve):
        self.AdditionalCurves.append(curve)
    def add_additional_curves(self, curves):
        self.AdditionalCurves.extend(curves)
    def update_image(self, image):
        self.Image = image
    def update_extent(self, extent):
        self.Extent = extent
    def set_custom_outline_curves(self, outline_curves):
        self.LocalOutlineCurves = outline_curves
    def plot_persistent(self, ax: "Axes", transform, z_order, outline_thickness=0.5, debug_colors=False):
        if debug_colors:
            outline_color = ImageSample.outline_colors[ImageSample.current_outline]
        else:
            outline_color = [0.0, 0.0, 0.0]
        for i in range(len(self.PersistentClipPaths)):
            im = ax.imshow(self.Image, extent=self.Extent, origin="upper", cmap="gray", clip_on=True, interpolation="bicubic",
                           zorder=z_order, transform=self.PersistentTransforms[i] + transform + ax.transData)
            im.set_clip_path(self.PersistentClipPaths[i], self.PersistentTransforms[i] + transform + ax.transData)
            for curve in self.LocalOutlineCurves:
                ax.plot(curve[:, 0], curve[:, 1], color=outline_color, solid_capstyle='round', transform=self.PersistentTransforms[i] + transform + ax.transData, zorder=z_order, linewidth=outline_thickness)
    def plot(self, ax: "Axes", z_order=1, outline_thickness=0.5, debug_colors=False):
        z_order += 0.001
        for curve in self.AdditionalCurves:
            ax.plot(curve[:, 0], curve[:, 1], color="k", solid_capstyle='round', zorder=z_order, transform=ax.transData, linewidth=outline_thickness)
        if self.LocalOutlineCurves:
                self.plot_persistent(ax, transform, z_order, outline_thickness, debug_colors)
                z_order += 0.001
        ImageSample.current_outline = (ImageSample.current_outline+1) % 20

class CodeSegmentExtractor:
    @staticmethod
    def extract_lines(block: str, middle_line_count: int = 1, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Generate examples by extracting specified middle lines with configurable context lines."""
        lines = block.splitlines()
        num_lines = len(lines)
        if num_lines < 2 * min_context_lines + middle_line_count:
            return []
        examples = []
        for middle_start in range(min_context_lines, num_lines - min_context_lines - middle_line_count):
            middle_end = middle_start + middle_line_count
            start_prefix = max(0, middle_start - (max_context_lines if max_context_lines >= 0 else middle_start))
            end_suffix = min(num_lines, middle_end + (max_context_lines if max_context_lines >= 0 else num_lines))
            indentation, middle_text = TextUtils.split_indentation('\n'.join(lines[middle_start:middle_end]))
            prefix = '\n'.join(lines[start_prefix:middle_start]) + '\n' + indentation
            suffix = '\n' + '\n'.join(lines[middle_end:end_suffix])
            examples.append((prefix, middle_text, suffix))
        return examples
    @staticmethod
    def extract_function_arguments(block: str, max_arguments: int = 5, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract function arguments with specified context and argument count limitations."""
        function_calls = find_function_calls_with_args(block)
        lines = block.splitlines()
        num_lines = len(lines)
        examples = []
        for call in function_calls:
            if len(call['arguments']) > max_arguments > 0:
                continue
            if call['start_line'] - 1 < min_context_lines or num_lines - call['end_line'] < min_context_lines:
                continue
            prefix, middle, suffix = TextUtils.extract_function_call_details(lines, call)
            if not middle:
                continue
            start = max(0, call['start_line'] - 1 - (max_context_lines if max_context_lines >= 0 else call['start_line'] - 1))
            end = min(num_lines, call['end_line'] + (max_context_lines if max_context_lines >= 0 else num_lines))
            prefix = '\n'.join(lines[start:call['start_line'] - 1] + [prefix])
            suffix = '\n'.join([suffix] + lines[call['end_line']:end])
            examples.append((prefix, middle, suffix))
        return examples
    @staticmethod
    def extract_section(lines: List[str], start_pos: (int, int), end_pos: (int, int), max_characters: int = -1,
                        min_context_lines: int = 1, max_context_lines: int = -1) -> Tuple[str, str, str] | None:
        """Extract a section of code with a given start and end position."""
        num_lines = len(lines)
        if start_pos[0] < 0 or start_pos[1] < 0 or end_pos[0] < 0 or end_pos[1] < 0 or start_pos[0] >= num_lines or start_pos[1] >= num_lines or end_pos[0] >= num_lines or end_pos[1] >= num_lines:
            return None
        middle_text = (
            lines[start_line][start_idx:end_idx] if start_line == end_line
            else '\n'.join(
                [lines[start_line][start_idx:]] + lines[start_line + 1:end_line] + [lines[end_line][:end_idx]]
            )
        )
        if 0 < max_characters < len(middle_text):
            return None
        start = max(0, start_line - (max_context_lines if max_context_lines >= 0 else start_line))
        end = min(num_lines, end_line + 1 + (max_context_lines if max_context_lines >= 0 else num_lines))
        prefix = '\n'.join(lines[start:start_line] + [lines[start_line][:start_idx]])
        suffix = '\n'.join([lines[end_line][end_idx:]] + lines[end_line + 1:end])
        return prefix, middle_text, suffix
    @staticmethod
    def extract_assignment_sections(block: str, max_characters: int = 100, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract assignment sections with character limit and surrounding context."""
        assignments = find_assignments(block)
        lines = block.splitlines()
        examples = []
        for start_pos, end_pos in assignments:
            result = CodeSegmentExtractor.extract_section(lines, start_pos, end_pos, max_characters, min_context_lines, max_context_lines)
            if result:
                examples.append(result)
        return examples
    @staticmethod
    def extract_condition_sections(block: str, max_characters: int = 100, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract condition sections with character limit and surrounding context."""
        conditions = find_conditions(block)
        lines = block.splitlines()
        examples = []
        for start_pos, end_pos in conditions:
            result = CodeSegmentExtractor.extract_section(lines, start_pos, end_pos, max_characters, min_context_lines, max_context_lines)
            if result:
                examples.append(result)
        return examples

