            'conditions',
            lambda block, max_c, max_pf: CodeSegmentExtractor.extract_condition_sections(block, max_characters=max_c, max_context_lines=max_pf),
        )

class CodeSegmentExtractor:
    @staticmethod
    def extract_lines(block: str, middle_line_count: int = 1, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Generate examples by extracting specified middle lines with configurable context lines."""
        lines = block.splitlines()
        num_lines = len(lines)
        if num_lines < 2 * min_context_lines + middle_line_count:
            return []
        examples = []
        for middle_start in range(min_context_lines, num_lines - min_context_lines - middle_line_count):
            middle_end = middle_start + middle_line_count
            start_prefix = max(0, middle_start - (max_context_lines if max_context_lines >= 0 else middle_start))
            end_suffix = min(num_lines, middle_end + (max_context_lines if max_context_lines >= 0 else num_lines))
            indentation, middle_text = TextUtils.split_indentation('\n'.join(lines[middle_start:middle_end]))
            prefix = '\n'.join(lines[start_prefix:middle_start]) + '\n' + indentation
            suffix = '\n' + '\n'.join(lines[middle_end:end_suffix])
            examples.append((prefix, middle_text, suffix))
        return examples
    @staticmethod
    def extract_function_arguments(block: str, max_arguments: int = 5, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract function arguments with specified context and argument count limitations."""
        function_calls = find_function_calls_with_args(block)
        lines = block.splitlines()
        num_lines = len(lines)
        examples = []
        for call in function_calls:
            if len(call['arguments']) > max_arguments > 0:
                continue
            if call['start_line'] - 1 < min_context_lines or num_lines - call['end_line'] < min_context_lines:
                continue
            prefix, middle, suffix = TextUtils.extract_function_call_details(lines, call)
            if not middle:
                continue
            start = max(0, call['start_line'] - 1 - (max_context_lines if max_context_lines >= 0 else call['start_line'] - 1))
            end = min(num_lines, call['end_line'] + (max_context_lines if max_context_lines >= 0 else num_lines))
            prefix = '\n'.join(lines[start:call['start_line'] - 1] + [prefix])
            suffix = '\n'.join([suffix] + lines[call['end_line']:end])
            examples.append((prefix, middle, suffix))
        return examples
    @staticmethod
    def extract_section(lines: List[str], start_pos: (int, int), end_pos: (int, int), max_characters: int = -1,
                        min_context_lines: int = 1, max_context_lines: int = -1) -> Tuple[str, str, str]:
        num_lines = len(lines)
        start_line, start_idx = start_pos[0] - 1, start_pos[1]
        end_line, end_idx = end_pos[0] - 1, end_pos[1]
        if start_line < min_context_lines or num_lines - end_line - 1 < min_context_lines:
            return None
        middle_text = (
            lines[start_line][start_idx:end_idx] if start_line == end_line
            else '\n'.join(
                [lines[start_line][start_idx:]] + lines[start_line + 1:end_line] + [lines[end_line][:end_idx]]
            )
        )
        if 0 < max_characters < len(middle_text):
            return None
        start = max(0, start_line - (max_context_lines if max_context_lines >= 0 else start_line))
        end = min(num_lines, end_line + 1 + (max_context_lines if max_context_lines >= 0 else num_lines))
        prefix = '\n'.join(lines[start:start_line]) + '\n' + lines[start_line][start_idx:]
        suffix = '\n' + '\n'.join(lines[end_line + 1:end]) + lines[end_line][end_idx:]
        return (prefix, middle_text, suffix)
    def extract_assignment_sections(block: str, max_characters: int = 100, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract assignment sections with character limit and surrounding context."""
        assignments = find_assignments(block)
        lines = block.splitlines()
        examples = []
        for start_pos, end_pos in assignments:
            result = CodeSegmentExtractor.extract_section(lines, start_pos, end_pos, max_characters, min_context_lines, max_context_lines)
            if result:
                examples.append(result)
        return examples
    @staticmethod
    def extract_condition_sections(block: str, max_characters: int = 100, min_context_lines: int = 1, max_context_lines: int = -1) -> List[Tuple[str, str, str]]:
        """Extract condition sections with character limit and surrounding context."""
        conditions = find_conditions(block)
        lines = block.splitlines()
        examples = []
        for start_pos, end_pos in conditions:
            result = CodeSegmentExtractor.extract_section(lines, start_pos, end_pos, max_characters, min_context_lines, max_context_lines)
            if result:
                examples.append(result)
        return examples

class TransformsGenerator:
    @staticmethod
    def rotational_repeat(repetitions: int = None, delta_angle: float = None, rotation_offset=0.0, use_flip=False,
                           flip_angle=0.0, reverse_rotation=False):
        if repetitions is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif repetitions is None:
            repetitions = int(np.trunc(2 * np.pi / delta_angle))
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, repetitions)
            angle = None
        else:
            repetitions = np.maximum(1, repetitions)
            angle = delta_angle
        if angle is None:
            angle = 2 * np.pi / repetitions
        if reverse_rotation:
            angle = -angle
        transforms = []
        for i in range(repetitions):
            if i % 2 == 0 or not use_flip:
                trans_data = Affine2D().rotate(i * angle + rotation_offset)
            else:
                trans_data = Affine2D().rotate(-flip_angle).scale(1, -1).rotate(i * angle + flip_angle + rotation_offset)
            transforms.append(trans_data)
        return transforms
    @staticmethod
    def circular_placement(radius=0.5, repetitions: int = None, delta_angle: float = None, local_rotation_offset=0.0,
                           global_rotation_offset=0.0, use_flip=False, flip_angle=0.0, reverse_rotation=False):
        if repetitions is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif repetitions is None:
            repetitions = np.trunc(2 * np.pi / delta_angle)
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, repetitions)
            angle = None
        else:
            repetitions = np.maximum(1, repetitions)
            angle = delta_angle
        if angle is None:
            angle = 2 * np.pi / repetitions
        if reverse_rotation:
            angle = -angle
        transforms = []
        for i in range(repetitions):
            if i % 2 == 0 or not use_flip:
                trans_data = Affine2D().rotate(local_rotation_offset).translate(radius, 0).rotate(i * angle + global_rotation_offset)
            else:
                trans_data = Affine2D().rotate(-flip_angle).scale(1, -1).rotate(flip_angle + local_rotation_offset).translate(radius, 0).rotate(i * angle + global_rotation_offset)
            transforms.append(trans_data)
        return transforms

class Figure:
    z_order = 0
    def __init__(self):
        self.Curves = []
        self.PlotType = []
        self.Images = []
        self.X_bounds = None
        self.Y_bounds = None
        self.Rho_bounds = None
        self.Theta_bounds = None
    @staticmethod
    def evaluate_curve_bounds(curve):
        x_bounds = (np.min(curve[0, :]), np.max(curve[0, :]))
        y_bounds = (np.min(curve[1, :]), np.max(curve[1, :]))
        (rho, theta) = CurveTransforms.cartesian_to_polar(curve[0, :], curve[1, :])
        rho_bounds = (np.min(rho), np.max(rho))
        theta_bounds = (np.min(theta), np.max(theta))
        return x_bounds, y_bounds, rho_bounds, theta_bounds
    def update_curve_bounds(self, bounds):
        if self.X_bounds is None:
            self.X_bounds = bounds[0]
            self.Y_bounds = bounds[1]
            self.Rho_bounds = bounds[2]
            self.Theta_bounds = bounds[3]
        else:
            self.X_bounds = (np.minimum(self.X_bounds[0], bounds[0][0]),
                             np.maximum(self.X_bounds[1], bounds[0][1]))
            self.Y_bounds = (np.minimum(self.Y_bounds[0], bounds[1][0]),
                             np.maximum(self.Y_bounds[1], bounds[1][1]))
            self.Rho_bounds = (np.minimum(self.Rho_bounds[0], bounds[2][0]),
                               np.maximum(self.Rho_bounds[1], bounds[2][1]))
            self.Theta_bounds = (np.minimum(self.Theta_bounds[0], bounds[3][0]),
                                 np.maximum(self.Theta_bounds[1], bounds[3][1]))
    def evaluate_bounds(self):
        for Curve in self.Curves:
            bounds = self.evaluate_curve_bounds(Curve)
            self.update_curve_bounds(bounds)
    def add_curve(self, curve, plot_type, bounds=None, evaluate_bounds=False):
        self.Curves.append(curve)
        self.PlotType.append(plot_type)
        if bounds:
            self.update_curve_bounds(bounds)
        elif evaluate_bounds:
            self.update_curve_bounds(self.evaluate_bounds())
    def append_figure(self, figure: "Figure"):
        self.Curves.extend(figure.Curves)
        self.PlotType.extend(figure.PlotType)
        self.update_curve_bounds((figure.X_bounds, figure.Y_bounds, figure.Rho_bounds, figure.Theta_bounds))
    def __iadd__(self, figure: "Figure"):
        if isinstance(figure, Figure):
            self.append_figure(figure)
        else:
            raise TypeError("Operator += can only be used with instance of class Figure")
    def __add__(self, figure: "Figure"):
        if isinstance(figure, Figure):
            new_figure = Figure()
            new_figure += self
            new_figure += figure
        else:
            raise TypeError("Operator + can only be used with instance of class Figure")
        return new_figure
    def plot(self, ax, image_outline_width=1.0):
        for image in self.Images:
            image.plot(ax, self.z_order, image_outline_width)
            Figure.z_order += 1
        for i in range(len(self.Curves)):
            if self.PlotType[i] == "clear":
                ax.fill(self.Curves[i][:, 0], self.Curves[i][:, 1], color="w", zorder=self.z_order)
            elif self.PlotType[i] == "fill":
                ax.fill(self.Curves[i][:, 0], self.Curves[i][:, 1], color="k", zorder=self.z_order)
            elif self.PlotType[i] > 0:
                ax.plot(self.Curves[i][:, 0], self.Curves[i][:, 1], color="k", solid_capstyle='round',
                         linewidth=self.PlotType[i], zorder=self.z_order)
            elif self.PlotType[i] < 0:
                ax.fill(self.Curves[i][:, 0], self.Curves[i][:, 1], color="w", zorder=self.z_order)
                ax.plot(self.Curves[i][:, 0], self.Curves[i][:, 1], color="k", solid_capstyle='round',
                         linewidth=self.PlotType[i], zorder=self.z_order)
        Figure.z_order += 1

class CurveGenerator:
    @staticmethod
    def bezier(control_points, values=np.linspace(0.0, 1.0, 100), polar=False):
        nodes = control_points
        if polar:
            nodes = CurveTransforms.polar_to_cartesian(control_points)
        nodes = np.asfortranarray(nodes)
        bezier_curve = bezier.Curve(nodes.T, degree=nodes.shape[0] - 1)
        return bezier_curve.evaluate_multi(values).T
    @staticmethod
    def math_function(function, values, input_coordinate="x_input", polar=False):
        if input_coordinate == "x_input":
            x_values = values
            y_values = np.array([function(x) for x in x_values])
            curve = np.vstack((x_values, y_values)).T
        elif input_coordinate == "y_input":
            y_values = values
            x_values = np.array([function(y) for y in y_values])
            curve = np.vstack((x_values, y_values)).T
        elif input_coordinate == "parametric":
            result = [function(value) for value in values]
            curve = np.array(result)
        else:
            print("Invalid input_coordinate, curve will default to 0,0")
            curve = np.array([[0.0, 0.0]])
        if polar:
            curve = CurveTransforms.polar_to_cartesian(curve)
        return curve
    @staticmethod
    def regular_polygon(n, radius=1.0, offset=(0.0, 0.0)):
        theta = np.linspace(0, 2 * np.pi, n + 1)
        rho = radius * np.ones(n + 1)
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def edge_type1(width=1.0, height=0.2, flip=False, half=False):
        if flip:
            height = -height
        triangle1 = np.array([[0, 0], [0.1*width, height], [0.2*width, 0]])
        triangle2 = np.array([[0.8*width, 0], [0.9*width, height], [width, 0]])
        curve_nodes = np.array([[0.2*width, 0], [0.4*width, height], [0.6*width, height], [0.8*width, 0]])
        curve = CurveGenerator.bezier(curve_nodes, np.linspace(0, 1 - 0.5 * half, 100))
        if half:
            return CurveTransforms.merge_curves([triangle1, curve])
        return CurveTransforms.merge_curves([triangle1, curve, triangle2])
    @staticmethod
    def random_edge(grid_size=(1,1), grid_step=(0.1, 0.1), interpolation_type="bezier"):
        control_nodes = []
        for i in range(grid_size[0] + 1):
            control_nodes.append([i * grid_step[0], np.random.randint(0, grid_size[1] + 1) * grid_step[1]])
        control_nodes = np.array(control_nodes)
        if interpolation_type == "bezier":
            curve = CurveGenerator.bezier(control_nodes)
        elif interpolation_type == "linear":
            curve = control_nodes
        else:
            curve = CurveTransforms.interpolate(control_nodes, 100, "cubic", False)
        return curve
    @staticmethod
    def polyline(x_values, y_values):
        return np.vstack((np.array(x_values), np.array(y_values))).T
    @staticmethod
    def circular_arc(n=100, radius=0.5, theta1=0.0, theta2=np.pi / 2, offset=(0.0, 0.0)):
        theta = np.linspace(theta1, theta2, n)
        rho = np.ones(100) * radius
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def sine_wave_circular_arc(n=100, radius=0.5, theta1=0.0, theta2=np.pi/2, amplitude=0.1, n_periods=2, period_offset=0.25, offset=(0.0, 0.0)):
        theta = np.linspace(theta1, theta2, n)
        period = (theta2 - theta1)/n_periods
        freq = 2*np.pi/period
        rho = radius + amplitude*np.sin((theta-theta1+period_offset*period)*freq)
        curve = CurveTransforms.polar_to_cartesian(np.vstack((rho, theta)).T)
        CurveTransforms.translate_single(curve, offset[0], offset[1], inplace=True)
        return curve
    @staticmethod
    def petal(n=100, width=0.1, height=0.2, flat_stop_height=0.1, half=True):
        arc = CurveGenerator.circular_arc(n, width / 2, 0, np.pi, (0, flat_stop_height))
        CurveTransforms.scale_single(arc, (1, 2 * (height - flat_stop_height) / width), (0, flat_stop_height))
    @staticmethod
    def leaf(values=np.linspace(0, 1, 100), width=0.1, height=0.2, half=False):
        control_nodes = np.array([[0, 0], [0, 1], [-1, 1], [-1, 2], [0, 2], [0, 3]])
        curve = CurveGenerator.bezier(control_nodes, values)
        curve = CurveTransforms.fit_c2c(curve, (-width/2, 0, 0, height))
        if not half:
            curve = np.vstack((curve, np.flip(CurveTransforms.scale_single(curve, (-1, 1), inplace=False), 0)))
        return curve
    @staticmethod
    def pit(n=100, width=0.8, gap=0.2, repetitions=1):
        start = -(width+gap)/2
        arc_start = -width/2
        end = -start
        arc_end = -arc_start
        gap1 = np.linspace(start, arc_start, 10)
        gap2 = np.linspace(arc_end, end, 10)
        gap1 = np.vstack((gap1, np.zeros(10))).T
        gap2 = np.vstack((gap2, np.zeros(10))).T
        curve = np.vstack((gap1, gap2))
        curves = []
        for i in range(repetitions):
            curves.append(CurveTransforms.translate_single(curve, i*(width+gap), 0, False))
        return CurveTransforms.merge_curves(curves)

class Transformer:
    @staticmethod
    def rotate_curves(curves: "list", angle=0):
        transform = Affine2D().rotate(angle)
        for i in range(len(curves)):
            curves[i] = transform.transform(curves[i])
    @staticmethod
    def rotate_figure(figure: "Figure", angle=0):
        Transformer.rotate_curves(figure.Curves, angle)
        figure.evaluate_bounds()
        return figure
    @staticmethod
    def scale_curves(curves: "list", scale=1.0):
        for i in range(len(curves)):
            curves[i] = scale*curves[i]
    @staticmethod
    def scale_figure(figure: "Figure", scale=1.0):
        Transformer.scale_curves(figure.Curves, scale)
        figure.evaluate_bounds()
    @staticmethod
    def rotation_repeat_curves(curves: "list", number_of_segments=None, delta_angle=None, scale=1.0, reverse_rotation=False):
        if number_of_segments is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif number_of_segments is None:
            repetitions = np.trunc(2*np.pi/delta_angle)
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, number_of_segments)
            angle = None
        else:
            repetitions = np.maximum(1, number_of_segments)
            angle = delta_angle
        if angle is None:
            angle = 2*np.pi/repetitions
        if reverse_rotation:
            angle = -angle
        scale = np.array(scale)
        if scale.size == 1:
            scales = scale*np.ones(repetitions)
        elif scale.size == 2:
            scales = np.linspace(scale[0], scale[1], repetitions)
        elif scale.size == repetitions:
            scales = scale
        else:
            scales = np.ones(repetitions)
        base_curves = copy.deepcopy(curves)
        Transformer.scale_curves(curves, scales[0])
        for i in range(1, repetitions):
            new_curves = copy.deepcopy(base_curves)
            Transformer.rotate_curves(new_curves, i*angle)
            Transformer.scale_curves(new_curves, scales[i])
            curves.extend(new_curves)
    @staticmethod
    def rotation_repeat_figure(figure: "Figure", number_of_segments=None, delta_angle=None, scale=1.0):
        Transformer.rotation_repeat_curves(figure.Curves)
        figure.evaluate_bounds()
    @staticmethod
    def flip_horizontal(curves):
        for i in range(len(curves)):
            curves[i][0, :] = -curves[i][0, :]
    @staticmethod
    def flip_vertical(curves):
        for i in range(len(curves)):
            curves[i][1, :] = -curves[i][1, :]
    @staticmethod
    def translate_curves(curves: "list", x=0.0, y=0.0):
        for i in range(len(curves)):
            curves[i][0, :] += x
            curves[i][1, :] += y
    @staticmethod
    def translation_repeat_curves(curves: "list", repetitions=(2, 2), step=(0.1, 0.1), scale=1.0):
        x_repetitions = np.maximum(repetitions[0], 1)
        y_repetitions = np.maximum(repetitions[1], 1)
        base_curves = copy.deepcopy(curves)
        for x in range(1, x_repetitions):
            Transformer.translate_curves(base_curves, step[0], 0)
            new_curves = copy.deepcopy(base_curves)
            curves.extend(new_curves)
        base_curves = copy.deepcopy(curves)
        for y in range(1, y_repetitions):
            Transformer.translate_curves(base_curves, 0, step[1])
            new_curves = copy.deepcopy(base_curves)
            curves.extend(new_curves)
    @staticmethod
    def merge_curves(curves: "list"):
        curve = np.hstack(curves)
        curves.clear()
        curves.append(curve)
    @staticmethod
    def reverse_curve(curve):
        curve[0, :] = -curve[0, :]
        curve[1, :] = -curve[1, :]
    @staticmethod
    def reverse_figure(figure: "Figure"):
        Transformer.reverse_curve(figure.Curves)
        figure.evaluate_bounds()
    @staticmethod
    def reverse_curves(curves: "list"):
        for i in range(len(curves)):
            Transformer.reverse_curve(curves[i])
    @staticmethod
    def interpolate_curve(curve, samples=100, kind="cubic"):
        spline = interp1d(np.linspace(0, 1, curve.shape[1]), curve, kind=kind)
        t_fine = np.linspace(0, 1, samples)
        x_fine, y_fine = spline(t_fine)
        curve.resize((2, samples), refcheck=False)
        curve[:] = np.vstack((x_fine, y_fine))
    def transform_figure(self, figure: "Figure"):
        return

class CurveTransforms:
    @staticmethod
    def apply_affine_single(curve: np.ndarray, affine: Affine2D, inplace=True) -> np.ndarray: 
        if inplace:
            curve[:] = affine.transform(curve)
            return curve
        else:
            return affine.transform(curve)
    @staticmethod
    def apply_affine_multi(curves: List[np.ndarray], affine: Affine2D, inplace=True) -> List[np.ndarray]:
        if inplace:
            for i in range(len(curves)):
                curves[i][:] = affine.transform(curves[i])
            return curves
        else:
            transformed_curves = []
            for i in range(len(curves)):
                transformed_curves.append(affine.transform(curves[i]))
            return transformed_curves
    @staticmethod
    def rotate_single(curve: np.ndarray, angle=0.0, pivot=(0.0, 0.0), inplace=True) -> np.ndarray:
        transform = Affine2D().translate(-pivot[0], -pivot[1]).rotate(angle).translate(pivot[0], pivot[1])
        return CurveTransforms.apply_affine_single(curve, transform, inplace)
    @staticmethod
    def rotate_multi(curves: List[np.ndarray], angle=0.0, pivot=(0.0, 0.0), inplace=True) -> List[np.ndarray]:
        transform = Affine2D().translate(-pivot[0], -pivot[1]).rotate(angle).translate(pivot[0], pivot[1])
        return CurveTransforms.apply_affine_multi(curves, transform, inplace)
    @staticmethod
    def translate_single(curve: np.ndarray, x=0.0, y=0.0, inplace=True) -> np.ndarray:
        transform = Affine2D().translate(x, y)
        return CurveTransforms.apply_affine_single(curve, transform, inplace)
    @staticmethod
