        x_bounds = (np.min(curve_cartesian[:, 0]), np.max(curve_cartesian[:, 0]))
        y_bounds = (np.min(curve_cartesian[:, 1]), np.max(curve_cartesian[:, 1]))
        return CurveTransforms.square_map_cartesian_to_polar(curve_cartesian, x_bounds, y_bounds, rho_bounds, theta_bounds)
    @staticmethod
    def remap_polar_to_polar(curve_polar, old_rho_bounds, old_theta_bounds, new_rho_bounds, new_theta_bounds):
        x = old_rho_bounds[0] + (old_rho_bounds[1] - old_rho_bounds[0]) * np.cos(old_theta_bounds[0] + (old_theta_bounds[1] - old_theta_bounds[0]) * curve_polar[:, 0])
        y = old_rho_bounds[0] + (old_rho_bounds[1] - old_rho_bounds[0]) * np.sin(old_theta_bounds[0] + (old_theta_bounds[1] - old_theta_bounds[0]) * curve_polar[:, 0])
        return np.vstack((x, y)).T
