    def tight_remap_polar_to_polar(curve_polar, rho_bounds, theta_bounds):
        old_rho_bounds = (np.min(curve_polar[:, 0]), np.max(curve_polar[:, 0]))
        old_theta_bounds = (np.min(curve_polar[:, 1]), np.max(curve_polar[:, 1]))
