class Transformer:
    @staticmethod
    def rotate_curves(curves: "list", angle=0):
        transform = Affine2D().rotate(angle)
        for i in range(len(curves)):
            curves[i] = transform.transform(curves[i])
    @staticmethod
    def rotate_figure(figure: "Figure", angle=0):
        Transformer.rotate_curves(figure.Curves, angle)
        figure.evaluate_bounds()
        return figure
    @staticmethod
    def scale_curves(curves: "list", scale=1.0):
        for i in range(len(curves)):
            curves[i] = scale*curves[i]
    @staticmethod
    def scale_figure(figure: "Figure", scale=1.0):
        Transformer.scale_curves(figure.Curves, scale)
        figure.evaluate_bounds()
    @staticmethod
    def rotation_repeat_curves(curves: "list", number_of_segments=None, delta_angle=None, scale=1.0, reverse_rotation=False):
        if number_of_segments is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif number_of_segments is None:
            repetitions = np.trunc(2*np.pi/delta_angle)
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, number_of_segments)
            angle = None
        else:
            repetitions = np.maximum(1, number_of_segments)
            angle = delta_angle
        if angle is None:
            angle = 2*np.pi/repetitions
        if reverse_rotation:
            angle = -angle
        scale = np.array(scale)
        if scale.size == 1:
            scales = scale*np.ones(repetitions)
        elif scale.size == 2:
            scales = np.linspace(scale[0], scale[1], repetitions)
        elif scale.size == repetitions:
            scales = scale
        else:
            scales = np.ones(repetitions)*np.ones(repetitions)
        for i in range(repetitions):
            new_curves = copy.deepcopy(curves)
            Transformer.rotate_curves(new_curves, i*angle)
            Transformer.scale_curves(new_curves, scales[i])
            curves.extend(new_curves)
    @staticmethod
    def rotation_repeat_figure(figure: "Figure", number_of_segments=None, delta_angle=None, scale=1.0):
        Transformer.rotation_repeat_curves(figure.Curves)
        figure.evaluate_bounds()
    @staticmethod
    def flip_horizontal(curves):
        for i in range(len(curves)):
            curves[i][0, :] = -curves[i][0, :]
    @staticmethod
    def flip_vertical(curves):
        for i in range(len(curves)):
            curves[i][1, :] = -curves[i][1, :]
    @staticmethod
    def translate_curves(curves: "list", x=0.0, y=0.0):
        for i in range(len(curves)):
            curves[i][0, :] += x
            curves[i][1, :] += y
    @staticmethod
    def translation_repeat_curves(curves: "list", repetitions=(2, 2), step=(0.1, 0.1), scale=1.0):
        x_repetitions = np.maximum(repetitions[0], 1)
        y_repetitions = np.maximum(repetitions[1], 1)
        base_curves = copy.deepcopy(curves)
        for x in range(1, x_repetitions):
            Transformer.translate_curves(base_curves, step[0], 0)
            new_curves = copy.deepcopy(base_curves)
            curves.extend(new_curves)
        base_curves = copy.deepcopy(curves)
        for y in range(1, y_repetitions):
            Transformer.translate_curves(base_curves, 0, step[1])
            new_curves = copy.deepcopy(base_curves)
            curves.extend(new_curves)
    @staticmethod
    def merge_curves(curves: "list"):
        curve = np.hstack(curves)
        curves.clear()
        curves.append(curve)
    @staticmethod
    def reverse_curve(curve):
        curve[:] = np.flip(curve, 1)
    @staticmethod
    def reverse_curves(curves: "list"):
        for i in range(len(curves)):
            Transformer.reverse_curve(curves[i])
    @staticmethod
    def interpolate_curve(curve, samples=100, kind="cubic"):
        spline = interp1d(np.linspace(0, 1, curve.shape[1]), curve, kind=kind)
        t_fine = np.linspace(0, 1, samples)
        x_fine, y_fine = spline(t_fine)
        curve.resize((2, samples), refcheck=False)
        curve[:] = np.vstack((x_fine, y_fine))
