        return

class TextUtils:
    @staticmethod
    def split_indentation(line: str) -> Tuple[str, str]:
        """Split the indentation from the code text."""
        indentation = line[:line.find('\n')]
        code_text = line[line.find('\n') + 1:]
        return indentation, code_text
    @staticmethod
    def extract_function_call_details(lines: List[str], function_call: dict) -> Tuple[str, str, str]:
        """Extract the components of a function call: prefix, arguments, and suffix."""
        current_line, start_idx, end_idx = (
            function_call['end_line'] - 1,
            function_call['end_col_offset'] - 2,
            function_call['end_col_offset'] - 1,
        )
        parentheses_balance = 1
        while parentheses_balance:
            char = lines[current_line][start_idx]
            parentheses_balance += 1 if char == ')' else -1 if char == '(' else 0
            start_idx -= 1
            if start_idx < 0:
                current_line -= 1
                start_idx = len(lines[current_line]) - 1
        prefix = '\n'.join(lines[function_call['start_line'] - 1:current_line] + [lines[current_line][:start_idx + 2]])
        arguments = ', '.join(function_call['arguments'])
        suffix = lines[function_call['end_line'] - 1][end_idx:]
        return prefix, arguments, suffix
    @staticmethod
    def random_split_middle_examples(examples: List[Tuple[str, str, str]], min_left: int = 5, min_right: int = 5) -> List[Tuple[str, str, str]]:
        """Randomly split the middle section of examples with specific constraints."""
        new_examples = []
        for example in examples:
            prefix, middle, suffix = example
            if len(middle) < min_left + min_right:
                continue
            while True:
                split_idx = random.randint(min_left, min_left + (len(middle) - min_right - min_left) // 2 + 1)
                if middle[split_idx - 1:split_idx + 1] != r'\n':
                    break
            prefix += middle[:split_idx]
            middle = middle[split_idx:]
            new_examples.append((prefix, middle, suffix))
        return new_examples

class CodeCompletionEvaluator:
    def __init__(self):
        self.rouge = Rouge()
    def exact_match(self, generated, expected):
        return int(generated.strip() == expected.strip())
    def bleu_score(self, generated, expected):
        generated_tokens = nltk.word_tokenize(generated)
        expected_tokens = nltk.word_tokenize(expected)
        return sentence_bleu([expected_tokens], generated_tokens)
    def rouge_score(self, generated, expected):
        scores = self.rouge.get_scores(generated, expected)[0]
        return scores['rouge-l']['f']
    def meteor_score(self, generated, expected):
        try:
            generated_tokens = nltk.word_tokenize(generated)
            expected_tokens = nltk.word_tokenize(expected)
            return meteor_score([expected_tokens], generated_tokens)
        except Exception as e:
            print(f"Error calculating METEOR score: {e}")
            return None
    def syntax_validity(self, code):
        try:
            ast.parse(code)
            return 1
        except SyntaxError:
            return 0
    def chrf_score(self, generated, expected):
        try:
            return sacrebleu.sentence_chrf(generated, [expected]).score / 100.0
        except Exception as e:
            print(f"Error calculating chrF score: {e}")
            return None
    def bert_score(self, generated, expected):
        try:
            P, R, F1 = bert_score([generated], [expected], lang='en', rescale_with_baseline=True)
            return F1.item()
        except Exception as e:
            print(f"Error calculating BERTScore: {e}")
            return None
    def evaluate(self, generated, expected, check_syntax=False):
        results = {}
        results['exact_match'] = self.exact_match(generated, expected)
        results['chrf'] = self.chrf_score(generated, expected)
        results['bleu'] = self.bleu_score(generated, expected)
        results['rouge'] = self.rouge_score(generated, expected)
        results['meteor'] = self.meteor_score(generated, expected)
        results['bert'] = self.bert_score(generated, expected)
        if check_syntax:
            results['syntax_valid'] = self.syntax_validity(generated)
        return results
    def evaluate_multi(self, generated: List[str], expected: List[str], check_syntax=False):
        length = len(generated)
        if length != len(expected):
            return None
        total = defaultdict(float)
        counts = defaultdict(int)
        for i in range(length):
            generated_text = generated[i]
            expected_text = expected[i]
            results = self.evaluate(generated_text, expected_text, check_syntax)
            for key, value in results.items():
                total[key] += value
                    counts[key] += 1
        averaged = {key: (total[key] / counts[key]) if counts[key] > 0 else None for key in total}
        return averaged

