        transforms = []
        for i in range(repetitions):
            if i % 2 == 0 or not use_flip:
                trans_data = Affine2D().rotate(i * angle + rotation_offset)
            else:
                trans_data = Affine2D().rotate(-flip_angle).scale(1, -1).rotate(i * angle + flip_angle + rotation_offset)
