class ImageSample:
    outline_colors = [plt.get_cmap("tab20")(i/20) for i in range(20)]
    random.shuffle(outline_colors)
    current_outline = 0
    def __init__(self, image, extent, main_clip_path, local_outline_curves: List[np.ndarray] = None):
        self.Image = image
        self.Extent = extent
        self.MainClipPath = main_clip_path
        self.PersistentClipPaths = [main_clip_path]
        self.PersistentTransforms = [Affine2D()]
        self.AdditiveTransforms = []
        if local_outline_curves:
            self.LocalOutlineCurves = local_outline_curves
        else:
            self.LocalOutlineCurves = []
        self.AdditionalCurves = []
    def clear_additive_transforms(self):
        self.AdditiveTransforms = []
    def add_additive_transform(self, transform):
        self.AdditiveTransforms.append(transform)
    def add_additive_transforms(self, transforms):
        self.AdditiveTransforms.extend(transforms)
    def add_additional_curve(self, curve):
        self.AdditionalCurves.append(curve)
    def add_additional_curves(self, curves):
        self.AdditionalCurves.extend(curves)
    def update_image(self, image):
        self.Image = image
    def update_extent(self, extent):
        self.Extent = extent
    def set_custom_outline_curves(self, outline_curves):
        self.LocalOutlineCurves = outline_curves
    def plot_persistent(self, ax: "Axes", transform, z_order, outline_thickness=0.5, debug_colors=False):
        if debug_colors:
            outline_color = ImageSample.outline_colors[ImageSample.current_outline]
        else:
            outline_color = [0.0, 0.0, 0.0]
        for i in range(len(self.PersistentClipPaths)):
            im = ax.imshow(self.Image, extent=self.Extent, origin="upper", cmap="gray", clip_on=True, interpolation="bicubic",
                           zorder=z_order, transform=self.PersistentTransforms[i] + transform + ax.transData)
            im.set_clip_path(self.PersistentClipPaths[i], self.PersistentTransforms[i] + transform + ax.transData)
            for curve in self.LocalOutlineCurves:
                ax.plot(curve[:, 0], curve[:, 1], color=outline_color, linewidth=outline_thickness, zorder=z_order + 0.001)
            if debug_colors:
                ImageSample.current_outline = (ImageSample.current_outline+1) % 20
                outline_color = ImageSample.outline_colors[ImageSample.current_outline]
        if len(self.AdditiveTransforms) == 0:
            self.plot_persistent(ax, Affine2D(), z_order, outline_thickness, debug_colors)
        else:
            for transform in self.AdditiveTransforms:
                self.plot_persistent(ax, transform, z_order, outline_thickness, debug_colors)
                z_order += 0.001
        ImageSample.current_outline = (ImageSample.current_outline+1) % 20

class Transformer:
    @staticmethod
    def rotate_curves(curves: "list", angle=0):
        transform = Affine2D().rotate(angle)
        for i in range(len(curves)):
            curves[i] = transform.transform(curves[i])
    @staticmethod
    def rotate_figure(figure: "Figure", angle=0):
        Transformer.rotate_curves(figure.Curves, angle)
        figure.evaluate_bounds()
        return figure
    @staticmethod
    def scale_curves(curves: "list", scale=1.0):
        for i in range(len(curves)):
            curves[i] = scale*curves[i]
    @staticmethod
    def scale_figure(figure: "Figure", scale=1.0):
        Transformer.scale_curves(figure.Curves, scale)
        figure.evaluate_bounds()
    @staticmethod
    def rotation_repeat_curves(curves: "list", number_of_segments=None, delta_angle=None, scale=1.0, reverse_rotation=False):
        if number_of_segments is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif number_of_segments is None:
            repetitions = np.trunc(2*np.pi/delta_angle)
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, number_of_segments)
            angle = None
        else:
            repetitions = np.maximum(1, number_of_segments)
            angle = delta_angle
        if angle is None:
            angle = 2*np.pi/repetitions
        if reverse_rotation:
            angle = -angle
        scale = np.array(scale)
        if scale.size == 1:
            scales = scale*np.ones(repetitions)
        elif scale.size == 2:
            scales = np.linspace(scale[0], scale[1], repetitions)
        elif scale.size == repetitions:
            scales = scale
        else:
            scales = np.ones(repetitions)
        base_curves = copy.deepcopy(curves)
        Transformer.scale_curves(curves, scales[0])
        for i in range(1, repetitions):
            new_curves = copy.deepcopy(base_curves)
            Transformer.rotate_curves(new_curves, i*angle)
            Transformer.scale_curves(new_curves, scales[i])
            curves.extend(new_curves)
    @staticmethod
    def rotation_repeat_figure(figure: "Figure", number_of_segments=None, delta_angle=None, scale=1.0):
        curves = copy.deepcopy(figure.Curves)
        Transformer.rotation_repeat_curves(curves, number_of_segments, delta_angle, scale)
