def rotational_repeat(repetitions: int = None, delta_angle: float = None, rotation_offset=0.0, use_flip=True, flip_angle=0.0, reverse_rotation=False):
        if repetitions is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif repetitions is None:
            repetitions = int(np.trunc(2 * np.pi / delta_angle))
            angle = None
        elif delta_angle is None:
            delta_angle = 2 * np.pi / repetitions
            repetitions = np.maximum(1, repetitions)
            angle = delta_angle
        if angle is None:
            angle = 2 * np.pi / repetitions
        if reverse_rotation:
            angle = -angle
        transforms = []
        for i in range(repetitions):
            if i % 2 == 0 or not use_flip:
                trans_data = Affine2D().rotate(i * angle + rotation_offset)
            else:
                trans_data = Affine2D().rotate(-flip_angle).scale(1, -1).rotate(i * angle + flip_angle + rotation_offset)
            transforms.append(trans_data)
        return transforms

def diamond_figure(x_bounds=(0.0, 1.0), y_bounds=(0.0, 1.0), subdivisions=(3, 3), line_width=1.0, fill=False):
        x_delta = np.abs(x_bounds[0] - x_bounds[1]) / subdivisions[0]
        y_delta = np.abs(y_bounds[0] - y_bounds[1]) / subdivisions[1]
        diamonds = []
        for x in np.linspace(x_bounds[0], x_bounds[1], subdivisions[0]):
            for y in np.linspace(y_bounds[0], y_bounds[1], subdivisions[1]):
                diamonds.append(Curve(x, y))
        Transformer.translation_repeat_curves(diamonds, subdivisions, (x_delta, y_delta))
        Transformer.translate_curves(diamonds, x_bounds[0], y_bounds[0])
        figure = Figure()
        for curve in diamonds:
            figure.add_curve(curve, line_width)
        if fill and line_width:
            for curve in diamonds:
                figure.add_curve(curve, 0)
        figure.evaluate_bounds()
        return figure

def rotation_repeat_curves(curves: "list", number_of_segments=None, delta_angle=None, scale=1.0, reverse_rotation=False):
        if number_of_segments is None and delta_angle is None:
            raise ValueError("Either number_of_segments or delta_angle must be specified")
        if number_of_segments is None:
            repetitions = np.trunc(2*np.pi/delta_angle)
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, number_of_segments)
            angle = None
        else:
            repetitions = np.maximum(1, number_of_segments)
            angle = delta_angle
        if angle is None:
            angle = 2*np.pi/repetitions
        if reverse_rotation:
            angle = -angle
        scale = np.array(scale)
        if scale.size == 1:
            scales = scale*np.ones(repetitions)
        elif scale.size == 2:
            scales = np.linspace(scale[0], scale[1], repetitions)
        elif scale.size == repetitions:
            scales = scale
        else:
            scales = np.ones(repetitions)
        base_curves = copy.deepcopy(curves)
        Transformer.scale_curves(curves, scales[0])
        for i in range(1, repetitions):
            new_curves = copy.deepcopy(base_curves)
            Transformer.rotate_curves(new_curves, i*angle)
            Transformer.scale_curves(new_curves, scales[i])
            curves.extend(new_curves)

def initialize_regions(image, mask):
    regions = {}
    for id in range(1, mask.max() + 1):
        region_mask = mask == id
        regions[id] = {
            'mask': region_mask,
            'neighbors': set(neighbouring_regions(mask, region_mask)),
            'color_histogram': create_color_histogram(image, region_mask),
            'size': region_mask.sum()
        }
    return regions
