def visit_Call(self, node):
        func_name = None
        if isinstance(node.func, ast.Name):
            func_name = node.func.id
        elif isinstance(node.func, ast.Attribute):
            parts = []
            current = node.func
            while isinstance(current, ast.Attribute):
                parts.insert(0, current.attr)
                current = current.value
            if isinstance(current, ast.Name):
                parts.insert(0, current.id)
            func_name = ".".join(parts)
        args = [ast.get_source_segment(self.source_code, arg) for arg in node.args]
        if func_name:
            self.function_calls.append({
                "function": func_name,
                "arguments": args,
                "start_line": node.lineno,
                "end_line": node.end_lineno,
                "col_offset": node.col_offset,
                "end_col_offset": node.end_col_offset
            })
        self.generic_visit(node)

def evaluate_multi(self, generated: List[str], expected: List[str], check_syntax=False):
        length = len(generated)
        if length != len(expected):
            return None
        total = defaultdict(float)
        counts = defaultdict(int)
        for gen, exp in zip(generated, expected):
            for key, value in self.evaluate(gen, exp, check_syntax).items():
                if value is not None:
                    total[key] += value
                    counts[key] += 1
        averaged = {key: (total[key] / counts[key]) if counts[key] > 0 else None for key in total}
        return averaged

