def fit_c2c(curve, extent):
        x_bounds = (np.min(curve[:, 0]), np.max(curve[:, 0]))
        y_bounds = (np.min(curve[:, 1]), np.max(curve[:, 1]))
        return CurveTransforms.remap_c2c(curve, x_bounds, y_bounds, extent[0:2], extent[2:4])

def remap_c2c(curve, old_x_bounds, old_y_bounds, new_x_bounds, new_y_bounds):
        transform = Affine2D().translate(-old_x_bounds[0], -old_y_bounds[0])
        transform = transform.scale((new_x_bounds[1]-new_x_bounds[0])/(old_x_bounds[1]-old_x_bounds[0]),
                                    (new_y_bounds[1]-new_y_bounds[0])/(old_y_bounds[1]-old_y_bounds[0]))
        transform = transform.translate(new_x_bounds[0], new_y_bounds[0])
        return transform.transform(curve)

def apply_affine_multi(curves: List[np.ndarray], affine: Affine2D, inplace=True) -> List[np.ndarray]:
        if inplace:
            for i in range(len(curves)):
                curves[i][:] = affine.transform(curves[i])
            return curves
        else:
            transformed_curves = []
            for i in range(len(curves)):
                transformed_curves.append(affine.transform(curves[i]))
            return transformed_curves

def rotational_repeat(repetitions: int = None, delta_angle: float = None, rotation_offset=0.0, use_flip=True, flip_angle=0.0, reverse_rotation=False):
        if repetitions is None and delta_angle is None:
            repetitions = 1
            angle = None
        elif repetitions is None:
            repetitions = int(np.trunc(2 * np.pi / delta_angle))
            angle = None
        elif delta_angle is None:
            repetitions = np.maximum(1, repetitions)
            angle = None
        else:
            repetitions = np.maximum(1, repetitions)
            angle = delta_angle
        if angle is None:
            angle = 2 * np.pi / repetitions
        if reverse_rotation:
            angle = -angle
        transforms = []
        for i in range(repetitions):
            if i % 2 == 0 or not use_flip:
                trans_data = Affine2D().rotate(i * angle + rotation_offset)
            else:
                trans_data = Affine2D().rotate(-flip_angle).scale(1, -1).rotate(i * angle + rotation_offset)
            transforms.append(trans_data)
        return transforms

